mapper.select:
select i.film_id as filmId,i.film_name as filmName,i.film_length as filmLength,i.actors,i.film_cats as filmCats,i.film_language as filmType,i.img_address as imgAddress
from mtime_hall_film_info_t i
where film_id = #{id}

/*缺少fiesIds*/
          SELECT i.film_id as filmId,i.film_name as filmName,i.film_length as filmLength,i.actors,i.film_cats as filmCats,i.film_language as filmType,i.img_address as imgAddress
          from mtime_hall_film_info_t i
          LEFT JOIN mtime_field_t f
          ON f.film_id = i.UUID
          WHERE f.UUID = #{id}
